import React, { useEffect, useState } from "react"
import axios from "axios"

const Tugas12 = () =>{
    const [dataMahasiswa,setDataMahasiswa] = useState(null)
    useEffect(() => {
    let fetch = async () => {
        try {
            let result = await axios.get(`https://backendexample.sanbercloud.com/api/student-scores`) 
            
            let data = result.data.map((res)=>{
                let {course,name,id,score}=res
                return{
                    id,
                    name,
                    score,
                    course
                }
            })
            setDataMahasiswa([...data])
        } catch (error) {
            console.log(error)
        }
        
    }
    fetch()
    }, [])
    const handleIndexScore = (param) =>{
        if (param >= 90) {
             return "A"
        }else if(param <=80){
            return "B"
        } 
        else{
            return "C"
        }
    }

    return(
        <>
        <div className = "container_table">
            <h2>Tabel Nilai Mahasiswa</h2>
                <table id="customers">
                    <thead>
                        <tr>
                            <th>No.</th>
                            <th>Nama</th>
                            <th>Matakuliah</th>
                            <th>Nilai</th>
                            <th>Indeks Nilai</th>
                            <th>Aksi</th>
                        </tr>
                    </thead>
                    <tbody>
                        { dataMahasiswa !== null && (
                            <>
                            {dataMahasiswa.map((res,index)=>{
                                return(
                                    <tr key={res.id} >
                                        <td>{index+1}</td>
                                        <td>{res.name}</td>
                                        <td>{res.course}</td>
                                        <td>{res.score}</td>
                                        <td>{handleIndexScore(res.score)}</td>
                                        <td>
                                            <button>edit</button>
                                            <button>delete</button>
                                        </td>
                                    </tr>
                                )
                            })}
                            </>
                        )}
                    </tbody>
                </table>
        </div>
        <div className="container_form">
            <h2>Form Nilai Mahasiswa</h2>
                <form method="POST">
                    <label htmlFor="fname">Nama</label>
                    <input type ="text" id="fname" name="name" placeholder="Your Name"/>

                    <label htmlFor="fname">Mata Kuliah</label>
                    <input type="number" id="fname" name="course" placeholder="Harganya"/>

                    <label htmlFor="fname">Nilai</label>
                    <input type="number" id="fname" name="score" placeholder="Beratnya"/>
                    
                    <input type="submit" value="submit"/>
                </form>
         </div>

        </>
    )
}
export default Tugas12
